// pages/nuclein/nuclein.js
Page({

  /**
   * 页面的初始数据
   */
  data: {
    basePath: getApp().globalData.basePath,
    test_address:[
      "吉祥小区东门核酸检测点",
      "成华大道二仙桥核酸检测点",
      "八里屯核酸检测点"
    ],
    test_time:[
      "2022年4月13日 8:00-12:00",
      "2022年4月13日 14:00-16:00",
      "2022年4月15日 8:00-12:00",
      "2022年4月15日 14:00-16:00"
    ],
    test_result:[
      {
        text:"绿码",
        image:"data:image/svg+xml;base64,",
      },
      {
        text:"黄码",
        image:"data:image/svg+xml;base64,",
      },
      {
        text:"红码",
        image:"data:image/svg+xml;base64,",
      },
    ],

    back_arrow:"data:image/svg+xml;base64,",

    down_arrow:"data:image/svg+xml;base64,",

    remind_image:"data:image/svg+xml;base64,",
    isSelect1:false,
    isSelect2:false,
    isCheck:false,
    type1:"",
    type2:"",
    remind:[
      "在接种新冠病毒疫苗48小时内暂不进行核算采样，待48小时后在进行采样",
      "核算检测结果出具前，请尽量减少流动，不参与聚集性活动，如有发热等不适症状，请主动到就近医院就诊",
      "请注意保持“一米线”距离，主动配合且佩戴口罩，听从工作人员指挥，有序排队，按要求进出场地",
    ]
  },

  backIndex:function(){
    wx.navigateBack({
      delta: 1
    })
  },

  select1:function(){
    var isSelect = this.data.isSelect1
    this.setData({ isSelect1:!isSelect})
    },
  //点击下拉框选项，选中并隐藏下拉框
  getType1:function(e){
    let value = e.currentTarget.dataset.type
    this.setData({
    type1: value,
    isSelect1: false,
    })
  },

  select2:function(){
    var isSelect = this.data.isSelect2
    this.setData({ isSelect2:!isSelect})
    },
  //点击下拉框选项，选中并隐藏下拉框
  getType2:function(e){
    let value = e.currentTarget.dataset.type
    this.setData({
    type2: value,
    isSelect2: false,
    })
  },

  changeCheck:function(){
    var isCheck = this.data.isCheck
    this.setData({
      isCheck:!isCheck
    })
  },

  submit:function(){
    var isCheck = this.data.isCheck
    if(isCheck == true){
      wx.showToast({
        title: '成功',
        icon: 'success',
        duration: 2000
      })
    }
    else{
      wx.showToast({
        title: '请认真阅读须知',
        icon: 'error',
        duration: 2000
      })
    }
  },
  /**
   * 生命周期函数--监听页面加载
   */
  onLoad: function (options) {

  },

  /**
   * 生命周期函数--监听页面初次渲染完成
   */
  onReady: function () {

  },

  /**
   * 生命周期函数--监听页面显示
   */
  onShow: function () {

  },

  /**
   * 生命周期函数--监听页面隐藏
   */
  onHide: function () {

  },

  /**
   * 生命周期函数--监听页面卸载
   */
  onUnload: function () {

  },

  /**
   * 页面相关事件处理函数--监听用户下拉动作
   */
  onPullDownRefresh: function () {

  },

  /**
   * 页面上拉触底事件的处理函数
   */
  onReachBottom: function () {

  },

  /**
   * 用户点击右上角分享
   */
  onShareAppMessage: function () {

  }
})
